policies.body:
forbid (
	principal,
	action == cloud::Action::"cluster.createEdit",
	resource
) when {
	context.cluster.cloudProviders.containsAny([cloud::cloudProvider::"aws"])
};

forbid (
	principal,
	action == cloud::Action::"project.edit",
	resource
) 
	when {
	context.project.ipAccessList.contains(ip("0.0.0.0/0"))
};

forbid (
principal,
action,
resource == cloud::cluster::"60f7b3b4e4b0f1b4b7f7b3b4"
) when {
resource in cloud::project::"664619d870c247237f4b86a6" &&
context.cluster.cloudProviders.containsAny([cloud::cloudProvider::"aws", cloud::cloudProvider::"azure"])
};
